a7 wp-block-uagb-image--layout-default wp-block-uagb-image--effect-static wp-block-uagb-image--align-none\"><figure class=\"wp-block-uagb-image__figure\"></figure></div>\n<!-- /wp:uagb/image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|40\",\"bottom\":\"var:preset|spacing|40\",\"left\":\"var:preset|spacing|40\",\"right\":\"var:preset|spacing|40\"},\"blockGap\":\"var:preset|spacing|40\"}},\"layout\":{\"type\":\"constrained\",\"contentSize\":\"\",\"justifyContent\":\"right\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:var(--wp--preset--spacing--40);padding-right:var(--wp--preset--spacing--40);padding-bottom:var(--wp--preset--spacing--40);padding-left:var(--wp--preset--spacing--40)\"><!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"bottom\":\"24px\"}}},\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"left\"}} -->\n<div class=\"wp-block-group\" style=\"margin-bottom:24px\"></div>\n<!-- /wp:group -->\n\n<!-- wp:heading {\"level\":3,\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|light-color\"}}},\"typography\":{\"lineHeight\":\"1.4\",\"fontStyle\":\"normal\",\"fontWeight\":\"500\",\"fontSize\":\"40px\"}},\"textColor\":\"light-color\"} -->\n<h3 class=\"wp-block-heading has-light-color-color has-text-color has-link-color\" style=\"font-size:40px;font-style:normal;font-weight:500;line-height:1.4\">Brža dostupnost informacija</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|light-color\"}}},\"spacing\":{\"margin\":{\"bottom\":\"28px\"}}},\"textColor\":\"light-color\"} -->\n<p class=\"has-light-color-color has-text-color has-link-color\" style=\"margin-bottom:28px\">Omogućava brzo pronalaženje relevantnih podataka, ušteda vremena za učenik